import { createContext,useState } from "react";

const ShoppingContext = createContext();

export const ShoppingContextProvider = ({children}) => {

    const [baskets,setBaskets] = useState([]);
    const [sums,setSums] = useState(0);
    let sum = 0;

    const emptyBasket = () => {
        return setBaskets([]);
    }

    const removeBasket = (id) => {
        const removeItem = baskets.filter((item) => item.id !== id);
        return setBaskets(removeItem);
    }

    const shoppingSum = (id) => {
        const basketCost = baskets.find((item) => item.id === id);
        sum = sum  +  parseFloat(basketCost.cost);
        setSums(sum); 
    }

    const values = {
        baskets,
        setBaskets,
        emptyBasket,
        removeBasket,
        sums,
        setSums,
        shoppingSum,
    };

    return(
        <ShoppingContext.Provider value={values}>{children}</ShoppingContext.Provider>
    );

}

export default ShoppingContext;
